mapper.select:

     and c.record_date = #{recordDate}
     and c.company_code like concat('%', #{companyCode}, '%')
     and c.area = #{area}
     and c.record_code like concat('%', #{recordCode}, '%')

     and c.name like concat('%', #{name}, '%')
     and c.project_ame like concat('%', #{projectAme}, '%') 
     and c.contact_name like concat('%', #{contactName}, '%')
     and c.contact_position = #{contactPosition}
     and c.contact_phone = #{contactPhone}
     and c.contact_email = #{contactEmail}
     and c.brand = #{brand}
     and c.info = #{info}
     and c.product_info like concat('%', #{productInfo}, '%')
     and c.status = #{status}

     and c.customer_level = #{customerLevel}
     and c.code_name like concat('%', #{codeName}, '%')
     and c.industry_division = #{industryDivision}
     and c.source = #{source}
     and c.area_code = #{areaCode}
     and u.user_name like concat('%', #{ownerUserId}, '%')
 and dt.dept_name like concat('%', #{deptName}, '%')


    AND date_format(c.allocation_date,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')


    AND date_format(c.allocation_date,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')


     and c.assign_number = #{assignNumber}
     and c.file_number like concat('%', #{fileNumber}, '%')
     and c.record_type = #{recordType}
order by c.create_time desc


